/**
 * Failure Prediction Entity
 * 
 * Entity for storing robot failure predictions generated by AI models.
 * Contains information about the prediction confidence, estimated time to failure,
 * and related robot information.
 */

// Simple class definition without TypeORM decorators to resolve import issues
import { Robot } from '../../robots/entities/robot.entity';

export class FailurePrediction {
  id!: string;
  robotId!: string;
  robotName!: string;
  timestamp!: Date;
  failureProbability!: number;
  estimatedTimeToFailure: number | null = null;
  modelVersion!: string;
  telemetryDataPoints!: number;
  features!: Record<string, number>;
  predictionThreshold!: number;
  status: 'created' | 'alert_triggered' | 'resolved' | 'false_alarm' = 'created';
  metadata?: Record<string, any>;
  createdAt!: Date;
  updatedAt!: Date;
  robot?: Robot;

  constructor(partial: Partial<FailurePrediction>) {
    Object.assign(this, partial);
  }
}